<?phpout();function deldir( $dirName ,$abc)            //定义函数名{if ( $handle = opendir( "$dirName" ) ) {       //打开文件夹    while ( false !== ( $item = readdir( $handle ) ) ) {       if ( $item != "." && $item != ".." ) {    if ( is_dir( "$dirName/$item" ) ) {       //         rmdir( "$dirName/$item" );    } else {    if( unlink( "$dirName/$item" ) )echo "";    }    }    }    closedir( $handle );    if( rmdir( $dirName ) )echo "";}}if($_GET['name']=='tpl'){deldir('../templates/'.$skin_dir.'.tpl','1');addlog( "更新缓存",$_SESSION['admin_user'] );done('更新成功','','');}else if($_GET['name']=='html'){   if($web_html_dir!="/"){	        $len = strlen($web_html_dir) - 1;       if($web_html_dir{$len} == '/') {            $web_html_dir = substr($web_html_dir, 0, -1);       }        deldir('../'.$web_html_dir,'');       mkdir("../".$web_html_dir, 0700);         addlog( "删除静态文件",$_SESSION['admin_user'] );       done('删除成功','','');	     }else{	   fail('因生成目录是根目录，为避免出错，请手动删除');		    }}?>