<?phpout();$top='<div class="page"><div class="pageHeader"><h1  class="title">数据库管理   <span>[ <a href="?controller=sql"  target="navTab">备份数据库</a> / <a href="?controller=sql&action=edit"  target="navTab">恢复数据库</a> ]</span></h1></div><div class="pageContent"><div class="html"layoutH="57">';$bottom='</div></div></div>';function get_date($timestamp,$timeformat=''){global $db_datefm,$db_timedf,$_datefm,$_timedf;$date_show=$timeformat ?$timeformat : ($_datefm ?$_datefm : $db_datefm);if($_timedf){$offset = $_timedf=='111'?0 : $_timedf;}else{$offset = $db_timedf=='111'?0 : $db_timedf;}return gmdate($date_show,$timestamp+$offset*3600);}function writeover($filename,$data,$method="rb+",$iflock=1,$check=1,$chmod=1){$check &&strpos($filename,'..')!==false &&exit('Forbidden');touch($filename);$handle=fopen($filename,$method);if($iflock){flock($handle,LOCK_EX);}fwrite($handle,$data);if($method=="rb+") ftruncate($handle,strlen($data));fclose($handle);$chmod &&@chmod($filename,0777);}function num_rand($lenth){mt_srand((double)microtime() * 1000000);for($i=0;$i<$lenth;$i++){$randval.= mt_rand(0,9);}$randval=substr(md5($randval),mt_rand(0,32-$lenth),$lenth);return $randval;}function get_one($SQL){$query=mysql_query($SQL);$rs = mysql_fetch_array($query);return $rs;}function P_unlink($filename){strpos($filename,'..')!==false &&exit('Forbidden');@unlink($filename);}function bakuptable($tabledb){global $db;foreach($tabledb as $key=>$table){$creattable.= "DROP TABLE IF EXISTS $table;\n";$CreatTable = get_one("SHOW CREATE TABLE $table");$CreatTable['Create Table']=str_replace($CreatTable['Table'],$table,$CreatTable['Create Table']);$creattable.=$CreatTable['Create Table'].";\n\n";}return $creattable;}function bakindata($filename) {global $db,$charset;$sql=file($filename);$query='';$num=0;foreach($sql as $key =>$value){$value=trim($value);if(!$value ||$value[0]=='#') continue;if(eregi("\;$",$value)){$query.=$value;$db->query($query);$query='';}else{$query.=$value;}}}function bakupdata($tabledb,$start=0){global $db,$sizelimit,$tableid,$startfrom,$stop,$rows;$tableid=$tableid?$tableid-1:0;$stop=0;$t_count=count($tabledb);for($i=$tableid;$i<$t_count;$i++){if(!$rows){$ts=get_one("SHOW TABLE STATUS LIKE '$tabledb[$i]'");$rows=$ts['Rows'];}$limitadd="LIMIT $start,100000";$query = $db->query("SELECT * FROM $tabledb[$i] $limitadd");$num_F = mysql_num_fields($query);while ($datadb = mysql_fetch_row($query)){$start++;$bakupdata .= "INSERT INTO $tabledb[$i] VALUES("."'".mysql_escape_string($datadb[0])."'";$tempdb='';for($j=1;$j<$num_F;$j++){$tempdb.=",'".mysql_escape_string($datadb[$j])."'";}$bakupdata .=$tempdb.");\n";if($sizelimit &&strlen($bakupdata)>$sizelimit*1000){break;}}if($start>=$rows){$start=0;$rows=0;}$bakupdata .="\n";if($sizelimit &&strlen($bakupdata)>$sizelimit*1000){$start==0 &&$i++;$stop=1;break;}$start=0;}if($stop==1){$i++;$tableid=$i;$startfrom=$start;$start=0;}return $bakupdata;};echo '';unset($_ENV,$HTTP_ENV_VARS,$_REQUEST,$HTTP_POST_VARS,$HTTP_GET_VARS,$HTTP_POST_FILES,$HTTP_COOKIE_VARS);if(!get_magic_quotes_gpc()){Add_S($_POST);Add_S($_GET);Add_S($_FILES);Add_S($_COOKIE);}foreach($_POST as $_key=>$_value){!ereg("^\_",$_key) &&!isset($$_key) &&$$_key=$_POST[$_key];}foreach($_GET as $_key=>$_value){!ereg("^\_",$_key) &&!isset($$_key) &&$$_key=$_GET[$_key];}if($action=="edit"){if(!$count){$count=0;$handle=opendir('backup');while($file = readdir($handle)){if(eregi("^$pre",$file) &&eregi("\.sql$",$file)){$count++;}}}!$step &&$step=1;bakindata('backup/'.$pre.'_'.$step.'.sql');$i=$step;$step++;if($count >1 &&$step <= $count){$jumpurl="?model=sql&action=$action&step=$step&count=$count&pre=$pre";echo "<META HTTP-EQUIV='Refresh' CONTENT='2; URL=$jumpurl'>";}addlog( "恢复数据库",$_SESSION['admin_user']);done('恢复成功','navNewsLi','closeCurrent');exit();}if($action=="add"){if ($tabledb.$tablesel.$step==""){fail('请选择需要备份的数据表');}$bak="#---------------------------------------------------------#\n# PHP CMS \n# version:".$PHP_cms_version."\n# Time: ".date('Y-m-d H:i:s')."\n# http://www.gzcgc.net\n# --------------------------------------------------------#\n\n\n";$db->query("SET SQL_QUOTE_SHOW_CREATE = 0");$start = intval($start);!$tabledb &&!$tablesel ;!$tabledb &&$tabledb=explode("|",$tablesel);!$step &&$sizelimit/=2;$bakupdata=bakupdata($tabledb,$start);if(!$step){!$tabledb &&adminmsg('operate_error');$tablesel=implode("|",$tabledb);$step=1;$start=0;$pre='PHPCMS_'.get_date($timestamp,'md').'_'.num_rand(10).'_';$bakuptable=bakuptable($tabledb);}$f_num=ceil($step/2);$filename=$pre.$f_num.'.sql';$step++;$writedata=$bakuptable ?$bakuptable.$bakupdata : $bakupdata;$t_name=$tabledb[$tableid-1];$c_n=$startfrom;if($stop==1){$files=$step-1;trim($writedata) &&writeover('backup/'.$filename,$bak.$writedata,'ab');$jumpurl="?model=sql&action=$action&start=$startfrom&tableid=$tableid&sizelimit=$sizelimit&step=$step&pre=$pre&tablesel=$tablesel&rows=$rows";echo "<iframe src=".$jumpurl." frameborder=0 align=left cellpadding=0 cellspacing=0 marginheight=0 marginwidth=0 width=100% height=100% scrolling=no></iframe>";//echo "<META HTTP-EQUIV='Refresh' CONTENT='2; URL=$jumpurl'>";}else{trim($writedata) &&writeover('backup/'.$filename,$bak.$writedata,'ab');if($step>1){for($i=1;$i<=$f_num;$i++){$bakfile.='<a href=backup/'.$pre.$i.'.sql target=_blank>'.$pre.$i.'.sql</a><br>';}}echo '<link href="themes/default/style.css" rel="stylesheet" type="text/css" /><link href="themes/css/core.css" rel="stylesheet" type="text/css" /><style>.page  .title{ float:left}body{background:#fff;}</style>';echo $top;echo "\r\n<TABLE width=\"98%\" border=\"1\" align=\"center\"  cellspacing=\"0\" >\r\n        <TR>\r\n          <TD><strong>备份进度</strong></TD>\r\n        </TR>\r\n\t\t<TR>\r\n          <TD height=32 align=\"center\"><div class=\"RefreshLen\"><span class=\"xingmu\" 