<?phpsession_start( );include_once "../lib/class.mysql.php";include_once "../data/config.php";include_once "../data/config_db.php";include_once "../lib/check.php";include_once "../lib/public.php";ob_start( );$action=verify_action($_GET['action']);$controller=verify_controller($_GET['controller']);$model=verify_model($_GET['model']);if($controller!='login'&&$model!="login"){admin_login($_SESSION['admin_user'],$_SESSION['admin_hits']);}$db->query( "SELECT * FROM {$DB_dbprefix}admin where user='{$_SESSION['admin_user']}'");if ( 0 <$db->next_record( ) ){$admin_id=$db->Record["id"];$admin_hits=$db->Record["hits"];$admin_user=$db->Record["user"];$admin_name=$db->Record["name"];$admin_salt=$db->Record["salt"];$admin_permissions=unserialize($db->Record["permissions"]);$admin_lasttime=$db->Record["lasttime"];$admin_lastip=$db->Record["lastip"];}$admin_permissions_ignore=array('login','index','upgrade','changepwd','');if(!in_array($controller.$model,$admin_permissions_ignore)){admin_permissions($controller.$model);}if($model==""&&$controller==""){header("Location:?controller=index");exit();}if($controller!=""){include_once( "controller/".$controller.".php");include_once( "templates/".$controller.".html");}if($model!=""){include_once( "model/".$model.".php");}?>