<?phprequire_once(dirname(__FILE__).'/header.php');$logs_gmt = $gmt_time - (86400 * 20); if($action == 'unlog'){ 	if($db->execute("DELETE FROM `{$DB['prefix']}logs` WHERE `addtime` < '{$logs_gmt}'")){		admin::logs(4,$language['page'][39].'，'.$language['common']['successful']); 		msgbox('','CURRENT');	} else {		admin::logs(4,$language['page'][39].'，'.$language['common']['failed']); 		msgbox($language['common']['failed'],'CURRENT');	}} elseif ($action == 'clear'){ 	include_once(CMSINC.'/tools/smarty/Smarty.class.php');	$clear = new Smarty();	$clear->compile_dir = CMSINC.'/tmp/translation_manage/';	$clear->clearCompiledTemplate();	msgbox($language['common']['successful'],'CURRENT');}$un_log = $db->query("SELECT count(*) FROM `{$db->prefix}logs` WHERE `addtime` < '{$logs_gmt}' ",1,0);$mycms['count'] = array(	'members' => get_count('members'), 	'article' => get_count('article'),	'product' => get_count('product'),	'picture' => get_count('picture'),	'download' => get_count('download'),	'guestbook'=> get_count('guestbook'),	'un_log' => is_numeric($un_log[0]) ? $un_log[0] : 0,);function get_count($table){	global $db;	$result = $db->query("SELECT count(*) FROM `{$db->prefix}{$table}`",1,0);	$result = is_numeric($result[0]) ? $result[0] : 0;	return $result;}function getcon($varName){	switch($res = get_cfg_var($varName)){		case 0 : return '<font color="red">×</font>'; break;		case 1 : return '<font color="green">√</font>'; break;		default : return $res; break;	}}$mycms['edition'] = MYCMS_EDITION; $mycms['version'] = MYCMS_VERSION;$mycms['release'] = MYCMS_RELEASE;$mycms['smarty_version'] = Smarty::SMARTY_VERSION;$mycms['authorization'] = MYCMS_AUTHORIZATION;$mycms['SERVER'] = $_SERVER;$mycms['PHP_VERSION'] = PHP_VERSION;$mycms['MySQL_VERSION'] = $db->version(false);$mycms['upload_max'] = getcon("upload_max_filesize");$mycms['post_max'] = getcon("post_max_size");$mycms['script_file'] = str_replace('\\','/',dirname(dirname(__FILE__)));$mycms['zend'] = (get_cfg_var("zend_optimizer.optimization_level") || get_cfg_var("zend_extension_manager.optimizer_ts") || get_cfg_var("zend_extension_ts")) ? '<font color="green">√</font>' : '<font color="red">×</font>';$disable_functions = get_cfg_var("disable_functions");$mycms['disable_functions'] = empty($disable_functions) ? '<em>NULL</em>' : $disable_functions;if(function_exists("gd_info") && extension_loaded('gd')){	$mycms['gd_info'] = gd_info();	$mycms['gd_info'] = $mycms['gd_info']['GD Version'];} else {	$mycms['gd_info'] = '<font color="#FF0000">'.$language['page'][31].'</font>'; }require_once(ADMIN_PATH.'/footer.php');