hp", 1 );				break;case "del" :				$db->query( "DELETE FROM {$DB_dbprefix}Classic WHERE id=".$_GET['id'] );				addlog( "删除精英", $_SESSION['user_name'] );				admin_showerr( "<li>精英删除成功</li>", "Admin_Classic.php", 1 );				break;default :				$db->query( "SELECT * FROM {$DB_dbprefix}Classic" );				echo "<table width=\"98%\" border=\"1\" align=\"center\" cellpadding=\"5\" cellspacing=\"0\" class=\"table\">\r\n  <tr>\r\n    <td width=\"5%\" align=\"center\"><strong>ID</strong></td>\r\n    <td align=\"center\"><strong>标题</strong></td>\r\n    <td width=\"18%\" align=\"center\"><strong>发布时间</strong></td>\r\n    <td width=\"15%\" align=\"center\"><strong>操作</strong></td>\r\n  </tr>\r\n";				while ( $db->next_record( ) )				{								echo "<form action=\"Admin_Classic.php\" method=\"post\" name=\"form";								echo $db->Record['id'];								echo "\">\r\n  <tr>\r\n    <td width=\"4%\" align=\"center\">";								echo $db->Record['id'];								echo "</td>\r\n    <td>";								echo $db->Record['title'];								echo "</td>\r\n    <td>";								echo $db->Record['time'];								echo "</td>\r\n    <td align=\"center\"><a href=\"Admin_Classic.php?Action=edit&id=";								echo $db->Record['id'];								echo "\">编辑</a> ｜\r\n    <a href=\"Admin_Classic.php?Action=del&id=";								echo $db->Record['id'];								echo "\">删除</a></td>\r\n  </tr>\r\n</form>\r\n";				}}echo "</table>\r\n</body>\r\n</html>";?>