<?phpfunction adds( ){				echo "<script language=\"javascript\" src=\"inc/js.js\"></script>\n<table width=\"98%\" border=\"1\" align=\"center\" cellpadding=\"5\" cellspacing=\"0\" class=\"table\">\r\n<form action=\"Admin_Links.php?Action=add\" method=\"post\" onSubmit=\"return Validator.Validate(this,3)\">\r\n  <tr>\r\n    <td width=\"15%\" align=\"right\">链接类型：</td>\r\n    <td>\r\n    <input name=\"type\" type=\"radio\"  value=\"0\" checked />\r\n      图片链接\r\n      \t\t\t\t<input name=\"type\" type=\"radio\" value=\"1\" />\r\n      文字链接</td>\r\n  </tr>\r\n  <tr>\r\n    <td align=\"right\">网站名称：</td>\r\n    <td><input name=\"linkname\" type=\"text\" size=\"40\" dataType=\"Require\" msg=\"请填写网站名称\"/></td>\r\n  </tr>\r\n  <tr>\r\n    <td align=\"right\">图片地址：</td>\r\n    <td><input name=\"linkimages\" type=\"text\" size=\"40\" id=\"linkimages\"/>\r\n    </td>\r\n  </tr>\r\n  <tr>\r\n    <td align=\"right\">网站地址：</td>\r\n    <td><input name=\"address\" type=\"text\" size=\"40\" dataType=\"Url\" msg=\"请填写正确的地址带有http://头\"/></td>\r\n  </tr>\r\n  <tr>\r\n    <td align=\"right\">网站介绍：</td>\r\n    <td><textarea name=\"introduction\" cols=\"45\" rows=\"5\" ></textarea></td>\r\n  </tr>\r\n  <tr>\r\n    <td colspan=\"2\" align=\"center\"><input type=\"submit\" name=\"Submit_add\" value=\"添加友情链接\" /></td>\r\n  </tr>\r\n</form>\r\n</table><br>\r\n";}function edits( ){				global $db,$DB_dbprefix;				$db->query( "select * from {$DB_dbprefix}links WHERE id=".$_GET['id'] );				if ( $db->next_record( ) )				{								echo "<script language=\"javascript\" src=\"inc/js.js\"></script>\n<table width=\"98%\" border=\"1\" align=\"center\" cellpadding=\"5\" cellspacing=\"0\" class=\"table\">\r\n<form action=\"Admin_Links.php?Action=edit\" method=\"post\" onSubmit=\"return Validator.Validate(this,3)\">\r\n  <tr>\r\n   <td width=\"15%\" align=\"right\">链接类型：</td>\r\n   <td>\r\n   <input name=\"id\" type=\"hidden\" value=\"";								echo $db->Record['id'];								echo "\"/>\r\n  