T['linkname']}','{$_POST['address']}','{$_POST['introduction']}','{$_POST['linkimages']}')" );				addlog( "添加友情链接", $_SESSION['user_name'] );				admin_showerr( "<li>添加友情链接成功</li>", "Admin_Links.php", 1 );				break;case "edits" :				edits( );				break;case "edit" :				$db->query( "UPDATE {$DB_dbprefix}links SET type='".$_POST['type']."',\r\n\t\tlinkname='{$_POST['linkname']}',\r\n\t\taddress='{$_POST['address']}',\r\n\t\tintroduction='{$_POST['introduction']}',\r\n\t\tlinkimages='{$_POST['linkimages']}' WHERE id={$_POST['id']}" );				addlog( "修改友情链接", $_SESSION['user_name'] );				admin_showerr( "<li>修改友情链接成功</li>", "Admin_Links.php", 1 );				break;case "del" :				$db->query( "DELETE FROM {$DB_dbprefix}links WHERE id=".$_GET['id'] );				addlog( "删除友情链接", $_SESSION['user_name'] );				admin_showerr( "<li>删除友情链接成功</li>", "Admin_Links.php", 1 );				break;default :				$db->query( "SELECT * FROM {$DB_dbprefix}links  order by id desc" );				echo "<table width=\"98%\" border=\"1\" align=\"center\" cellpadding=\"5\" cellspacing=\"0\" class=\"table\">\r\n<form action=\"Admin_Links.php\" method=\"post\" name=\"form";				echo $db->Record['id'];				echo "\">\r\n  <tr align=\"center\">\r\n    <td width=\"5%\"><strong>ID</strong></td>\r\n    <td width=\"12%\"><strong>链接类型</strong></td>\r\n    <td><strong>网站名称</strong></td>\r\n    <td><strong>网站地址</strong></td>\r\n    <td width=\"12%\"><strong>操作</strong></td>\r\n  </tr>\r\n";				while ( $db->next_record( ) )				{								echo "  <tr>\r\n    <td align=\"center\">";								echo $db->Record['id'];								echo "</td>\r\n    <td align=\"center\">";								if ( $db->Record['type'] == 0 )								{												echo "图片链接";								}								else								{												echo "文字链接";								}								echo "</td>\r\n    <td>";								echo $db->Record['linkname'];								echo "</td>\r\n    <td>";								echo $db->Record['address'];								echo "</td>\r\n    <td align=\"center\"><a href=\"Admin_Links.php?Action=edits&id=";								echo $db->Record['id'];								echo "\">修改</a> ｜<a href=\"Admin_Links.php?Action=del&id=";								echo $db->Record['id'];								echo "\">删除</a></td>\r\n  </tr>\r\n";				}				echo "</form>\r\n</table>\r\n";}echo "</body>\r\n</html>\r\n";?>