<?phpinclude( "inc/header.php" );login_check();function removeDir($dirName) {//删除Cache文件夹       $result = false;       if(! is_dir($dirName)) {		   addlog( "更新缓存", $_SESSION['user_name'] );		   admin_showerr( "<li>缓存更新成功</li>", "Admin_Sysinfo.php", 1 );		   } 	   $handle = opendir($dirName);	   while(($file = readdir($handle)) !== false)	   {		   if($file != '.' && $file != '..') {			   $dir = $dirName . DIRECTORY_SEPARATOR . $file; is_dir($dir) ? $this->removeDir($dir) : unlink($dir);			   } 		} 		closedir($handle); $result = rmdir($dirName) ? 		true : false; 		addlog( "更新缓存", $_SESSION['user_name'] );		admin_showerr( "<li>缓存更新成功</li>", "Admin_Sysinfo.php", 1 );		}		removeDir('../templates/'.$skindir.'.tpl');?>