<?phpfunction modulecolumns($_moduleid){	if ($_moduleid==99)		  {echo "外部链接";}		  else	if ($_moduleid==1)		  {echo "单页模块";}		  else    if($_moduleid==2)          {echo "文章模块";}		  else    if($_moduleid==3)          {echo "案例模块";}		  else    if($_moduleid==4)          {echo "招聘模块";}	if($_moduleid==5)          {echo "下载模块";}}function eidtmodule($_eidtmodule){	echo "<select name=\"module\" dataType=\"Require\" msg=\"请选择栏目类型\">\n";	{	if ($_eidtmodule==1)		  {echo "<option value='1'>单页模块</option>\n";}		  else    if($_eidtmodule==2)          {echo "<option value='2'>文章模块</option>\n";}		  else    if($_eidtmodule==3)          {echo "<option value='3'>案例模块</option>\n";}		  else    if($_eidtmodule==4)          {echo "<option value='4'>招聘模块</option>\n";}	if($_eidtmodule==5)          {echo "<option value='5'>下载模块</option>\n";}    if($_eidtmodule==99)          {echo "<option value='99'>外部链接</option>\n";}}	echo "<option>-----选择栏目类型-----</option>\n";	echo "<option value='1'>单页模块</option>\n";	echo "<option value='2'>文章模块</option>\n";	echo "<option value='3'>案例模块</option>\n";	echo "<option value='4'>招聘模块</option>\n";	echo "<option value='5'>下载模块</option>\n";	echo "<option value='99'>外部链接</option>\n";	echo "</select>\n";}function moduleurl($_moduleid){	if ($_moduleid==99)		  {echo "module/turl.php?Action=edits&id=";}		  else	if ($_moduleid==1)		  {echo "module/single.php?Action=edits&id=";}		  else    if($_moduleid==2)          {echo "module/article.php?Action=adds&id=";}		  else    if($_moduleid==3)          {echo "module/product.php?Action=adds&id=";}		  else    if($_moduleid==4)          {echo "module/hr.php?Action=adds&id=";}		  else    if($_moduleid==5)          {echo "module/download.php?Action=adds&id=";}}function echomodule($_echomodule){				global $db1,$DB_dbprefix;				$db1->query( "SELECT * FROM `{$DB_dbprefix}columns` where id=".$_echomodule." " );				if ( $db1->next_record( ) )				{    if ($db1->Record['module']==99)		  {echo "module/turl.php?Action=edits&id=";}		  else	if ($db1->Record['module']==1)		  {echo "module/single.php?Action=edits&id=";}		  else    if($db1->Record['module']==2)          {echo "module/article.php?Action=edits&id=";}		  else    if($db1->Record['module']==3)          {echo "module/product.php?Action=edits&id=";}		  else    if($db1->Record['module']==4)          {echo "module/hr.php?Action=edits&id=";}		  else    if($db1->Record['module']==5)          {echo "module/download.php?Action=edits&id=";}				}				}?>