<?phpfunction login_check( ){global $tun2_id,$tun2_user;$timeout=1200;     //超时时间,单位:秒,这里设为20分钟. if((time()-$_SESSION['session_time'])> $timeout) {     session_unset( );    session_destroy( );    @header( "Location:login.php" );    exit( );}if ($_SESSION['user_name']=="null"){	session_unset( );    session_destroy( );	@header( "Location:login.php" );    exit( );}if ($_SESSION['uid']=="null"){	session_unset( );    session_destroy( );	@header( "Location:login.php" );    exit( );}if($uc_switch=="1"){if ($tun2_user!=$_SESSION['user_name']){	session_unset( );    session_destroy( );	@header( "Location:login.php" );    exit( );}if ($tun2_id!=$_SESSION['uid']){	session_unset( );    session_destroy( );	@header( "Location:login.php" );    exit( );}if(!$tun2_user) {	session_unset( );    session_destroy( );	@header( "Location:login.php" );    exit( );    }}}?>