<?php@header("Content-Type:text/html;charset=utf-8");include_once "../../../inc/config.php";include_once "../../../inc/config_db.php"; $phpself =$_GET['pic']; $str = end(explode(".",$phpself)); $type   =   array("gif","jpg","png","bmp");        if   (!in_array($str,$type)) {   echo "<a href='www.tun2.com'>tun2企业网站管理系统</a>出错喇！";  exit(); }if($_GET['pic']==""){  echo "<a href='www.tun2.com'>tun2企业网站管理系统</a>出错喇！";  exit();} if(array_intersect(str_word_count($_GET['pic'],   1),   array( 'http'))) { $image = $_GET['pic'];  } else { $image = $web_url.$webdir.$_GET['pic'];  } //获得图片基本信息   $img = getimagesize($image);   switch ($img[2]) {       case 1 :           $im = @ imagecreatefromgif($image);           break;       case 2 :           $im = @ imagecreatefromjpeg($image);           break;       case 3 :           $im = @ imagecreatefrompng($image);           break;   }     $smallimg = imagecreatetruecolor(35,25);   imagecopyresized($smallimg,$im,0,0,0,0,70,25,$img[0],$img[1]);   //设置内容格式为images/jpeg   @header("Content-Type: image/jpeg");   //输出jpeg图片   imagecolorallocate ( $smallimg , 105 , 255 , 255 );imagejpeg($smallimg);  ?>