<?phpdefined('CMSROOT') or die('Access Denied.');$MYCMS->assign('config',$config);$MYCMS->assign('web',$web); $admin_config['theme'] = in_array($admin_config['theme'][$lang],array('blue','gray','green','red')) ? $admin_config['theme'][$lang] : 'green';$MYCMS->assign('admin',$admin_config);$MYCMS->assign('lang',$language); $mycms['session'] = $_SESSION;$mycms['get'] = $_GET;$mycms['cookie'] = $_COOKIE;require_once(CMSINC.'/config/shortcut.config.php');empty($shortcut[$PHP_SELF]) or $mycms['icon'] = admin::icon($shortcut[$PHP_SELF]); $MYCMS->assign('mycms',$mycms);isset($template_file) or $template_file = basename($PHP_SELF,'.php');$template_file .= $admin_config['template_suffix'];$fp = $MYCMS->template_dir[0].$template_file;is_file($fp) or exit($language['common']['not_template_file'].$fp);if($opp_info === false){	$MYCMS->display($template_file);} else {	die($MYCMS->fetch($template_file).$opp_info);}unset($LANGUAGE,$mycms,$language,$config,$web);is_object($db) && $db->close();