<?phprequire_once(dirname(__FILE__).'/header.php');if(isset($_POST['name'])){	$copyright = $_POST['copyright']; 	$_POST = html_chars($_POST);	$_POST['copyright'] = $copyright;	$field = $val = $field_val = NULL;	foreach($_POST as $i => $n){		$field .= '`'.$i.'`,'; 		$val .= "'".$n."',";		$field_val .= '`'.$i."` = '".$n."',";	}	$field .= '`lang`';	$val .= "'{$lang}'";	$field_val .= "`lang` = '{$lang}'";	if($db->execute("INSERT INTO `{$DB['prefix']}website` ({$field}) values ({$val}) ON DUPLICATE KEY UPDATE {$field_val}")){		admin::logs(2,$language['page']['log']."，{$language['common']['successful']}"); 		msgbox($language['common']['successful'],'CURRENTS'); 	} else {		admin::logs(2,$language['page']['log']."，{$language['common']['failed']}"); 		msgbox($language['common']['failed'],'CURRENTS'); 	}}$mycms['template_list'] = template::get_list();$lang_path = '../inc/language/frontend/';$folder = scan_dir($lang_path);$mycms['lang_dir'] = array();foreach($folder as $n){	is_dir($lang_path.$n) && $mycms['lang_dir'][] = $n;}$web['copyright'] = html_chars($web['copyright']);require_once(ADMIN_PATH.'/footer.php'); 