<?phprequire_once(dirname(__FILE__).'/header.php');$referer = empty($_SERVER['HTTP_REFERER']) ? './' : $_SERVER['HTTP_REFERER']; switch ($a){	case 'switch' : 		$admin_config['theme'][$lang] = $_GET['theme'];		if(array2php($admin_config,'admin_config',CMSINC.'/config/admin.config.php')){			msgbox('',$referer);		} else {			msgbox($language['page']['failure'],'BACK');		} 	break;	case 'lang' : 		($_GET['i'] > $config['lang_total'] || $_GET['i'] < 1) && die('Parameter Error.');		setcookie('MYCMS_global_language',$_GET['i'],$gmt_time + 2592000,'/'); 		$referer = parse_url($referer);		$referer = $referer['path'];		header('Location: ' . $referer);		exit();		break;	case 'phpinfo' : 		phpinfo();		die();	break;	case 'install' : 		@set_time_limit(0);		$zipname = basename($zipurl = $_POST['zipurl']); 		$tmp_zip = CMSINC.'/tmp/other/'.$zipname;		if(@file_put_contents($tmp_zip,@file_get_contents($zipurl))){			$remplate = new pclzip($tmp_zip);			$v_result_list = $remplate->extract(PCLZIP_OPT_PATH,CMSROOT.'/',PCLZIP_OPT_REPLACE_NEWER); 			@unlink($tmp_zip); 			if ($v_result_list == 0) {				die('var result = 2;'); 			} else {				die('var result = 9;'); 			}		} else {			die('var result = 1;'); 		}	break;	case 'cache' : 		include_once(CMSINC.'/tools/smarty/Smarty.class.php');		$MYCMS = new Smarty();		$MYCMS->cache_dir  = CMSINC.'/tmp/ /'; 		$MYCMS->clearAllCache();		msgbox($language['common']['successful'],$referer);	break;	case 'compile' : 		include_once(CMSINC.'/tools/smarty/Smarty.class.php');		$MYCMS = new Smarty();		$MYCMS->compile_dir = CMSINC.'/tmp/translation_frontend/';		$MYCMS->clearCompiledTemplate();		msgbox($language['common']['successful'],$referer);	break;	default: die('Undefined Operation.'); break;}