<?phprequire_once('../inc/include/header.php');$MYCMS->caching = false; USER_LOGIN or msgbox('',$config['url'].'member/login.php?goto='.base64_encode($_SERVER['REQUEST_URI']));if(isset($_POST['old_password'])){	$cms_user_info['password'] == md5(md5($_POST['old_password']).md5($cms_user_info['encryption'])) or msgbox($language['page']['msg'][6]); 	!empty($cms_user_info['problem']) && $cms_user_info['answer'] != $_POST['old_answer'] && msgbox($language['page']['msg'][7]); 	is_email($_POST['email']) or msgbox($language['page']['msg'][2]); 	$cms_user_info['email'] != $_POST['email'] && member::is_used($_POST['email'],1) && msgbox($language['page']['msg'][8]); 	!empty($_POST['new_password']) && !member::check_password($_POST['new_password']) && msgbox($language['page']['msg'][4]); 	(!empty($_POST['new_problem']) && empty($_POST['new_answer'])) || (empty($_POST['new_problem']) && !empty($_POST['new_answer'])) && msgbox($language['page']['msg'][5]); 	$sql = "UPDATE `{$DB['prefix']}members` SET `email` = '{$_POST['email']}' ";	empty($_POST['new_password']) or $sql .= ",`password` = '".md5(md5($_POST['new_password']).md5($cms_user_info['encryption']))."' ";	empty($_POST['new_answer']) or $sql .= ",`problem` = '{$_POST['new_problem']}',`answer` = '{$_POST['new_answer']}' ";	$sql .= "WHERE `id` = '".USER_ID."'";	if($db->execute($sql)){		$n_pwd = empty($_POST['new_password']) ? $cms_user_info['password'] : md5(md5($_POST['new_password']).md5($cms_user_info['encryption']));		setcookie('MYCMS_user_auth',encryption(USER_ID.$n_pwd.$cms_user_info['encryption'],'ENCOD',WEBKEY),0,'/');		msgbox($language['page']['msg'][9],'pwd_modify.php');	} else {		msgbox($language['page']['msg'][10]);	}}require_once(CMSINC.'/include/footer.php');