<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"><html xmlns="http://www.w3.org/1999/xhtml"><head><meta http-equiv="Content-Type" content="text/html; charset=utf-8" /><title>无标题文档</title><style type="text/css">body,td,th {	font-family: '微软雅黑', Arial, Helvetica, sans-serif;}#msg {	width: 200px;	margin-top: 10px;	margin-right: auto;	margin-bottom: 0px;	margin-left: auto;	text-align: center;}</style></head><script type="text/javascript" src="md5.js"></script><script language="javascript">function saveUserInfo(){//获取接受返回信息层var msg = document.getElementById("msg");//获取表单对象和用户信息值var f = document.user_info;var uid = f.uid.value;var gid = f.gid.value;var sid = f.sid.value;var photo = f.photo.value;var Gameworktype = f.Gameworktype.value;var title = f.title.value;var source = f.source.value;var key = hex_md5('gz4a'+source+uid);var Description = f.Description.value;var nickname = f.nickname.value;var tel = f.tel.value;//接收表单的URL地址var url = "http://a.quanjing.com/api/JoinGame.aspx";//需要POST的值，把每个变量都通过&来联接var postStr    = "uid="+ uid +"&gid="+ gid +"&sid="+ sid+"&photo="+ photo +"&Gameworktype="+ Gameworktype +"&title="+ title+ "&source="+ source +"&key="+ key+"&Description="+ Description +"&nickname="+ nickname +"&title="+ tel;           var ajax = false;          //开始初始化XMLHttpRequest对象          if(window.XMLHttpRequest) { //Mozilla 浏览器                  ajax = new XMLHttpRequest();                  if (ajax.overrideMimeType) {//设置MiME类别                          ajax.overrideMimeType("text/xml");                  }          }          else if (window.ActiveXObject) { // IE浏览器                  try {                          ajax = new ActiveXObject("Msxml2.XMLHTTP");                  } catch (e) {                          try {                                  ajax = new ActiveXObject("Microsoft.XMLHTTP");                          } catch (e) {}                  }          }          if (!ajax) { // 异常，创建对象实例失败                  window.alert("不能创建XMLHttpRequest对象实例.");                  return false;          }                                             //通过Post方式打开连接ajax.open("POST", url, true);//定义传输的文件HTTP头信息ajax.setRequestHeader("Content-Type","application/x-www-form-urlencoded");//发送POST数据ajax.send(postStr);//获取执行状态	 ajax.onreadystatechange = function(){  			if (ajax.readyState == 4 && ajax.status == 200){  				   msg.innerHTML = ajax.responseText;  				}  		  } 	  }</script><body><form  name="user_info" method="post" action="">  <table width="600" border="0" align="center" cellpadding="3" cellspacing="3">    <tr>      <td width="161">用户ID:<br />      (用户常用电子邮箱)</td>      <td width="418"><input type="text" name="uid" id="uid" /></td>    </tr>    <tr>      <td>参赛类别ID:</td>      <td><input type="text" name="gid" id="gid" /></td>    </tr>    <tr>      <td>参赛主题ID:</td>      <td><input type="text" name="sid" id="sid" /></td>    </tr>    <tr>      <td>图片地址:</td>      <td><input type="text" name="photo" id="photo" /></td>    </tr>    <tr>      <td>图片和视频标识:</td>      <td><select name="Gameworktype" id="Gameworktype">        <option value="1">图片</option>        <option value="2">视频</option>      </select></td>    </tr>    <tr>      <td>图片标题:</td>      <td><input type="text" name="title" id="title" /></td>    </tr>    <tr>      <td>合作网站标识:</td>      <td><input type="text" name="source" id="source" /></td>    </tr>    <tr>      <td>描述:</td>      <td><input type="text" name="Description" id="Description" /></td>    </tr>    <tr>      <td>用户匿称:</td>      <td><input type="text" name="nickname" id="nickname" /></td>    </tr>    <tr>      <td>联系电话:</td>      <td><input type="text" name="tel" id="tel" /></td>    </tr>    <tr>      <td colspan="2" align="center" valign="middle"><input type="button" value="提交表单" onClick="saveUserInfo()">&nbsp;&nbsp;&nbsp;&nbsp;<input name="" type="reset" value="重置" /> </td>    </tr>  </table>  <div id="msg">返回值</div></form></body></html>