E_D("DROP TABLE IF EXISTS `cms_article`;");
E_C("CREATE TABLE `cms_article` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `lang` tinyint(3) unsigned NOT NULL DEFAULT '1',
  `channel` int(10) unsigned NOT NULL DEFAULT '0',
  `category` varchar(255) NOT NULL,
  `title` varchar(255) NOT NULL,
  `brief` varchar(50) NOT NULL,
  `color` varchar(7) NOT NULL,
  `bold` tinyint(1) unsigned NOT NULL DEFAULT '0',
  `tag` varchar(100) NOT NULL,
  `author` varchar(50) NOT NULL,
  `source` varchar(50) NOT NULL,
  `sourceurl` varchar(50) NOT NULL,
  `keyword` varchar(100) NOT NULL,
  `introduction` text NOT NULL,
  `content` mediumtext NOT NULL,
  `template` varchar(50) NOT NULL,
  `comment` tinyint(1) unsigned NOT NULL DEFAULT '0',
  `commenttotal` int(10) unsigned NOT NULL DEFAULT '0',
  `picture` text NOT NULL,
  `recom` tinyint(1) unsigned NOT NULL DEFAULT '0',
  `published` tinyint(1) unsigned NOT NULL DEFAULT '0',
  `audit` tinyint(1) unsigned NOT NULL DEFAULT '0',
  `recycle` tinyint(1) unsigned NOT NULL DEFAULT '0',
  `permission` varchar(255) NOT NULL,
  `filename` varchar(50) NOT NULL,
  `click` int(10) unsigned NOT NULL DEFAULT '0',
  `money` decimal(8,2) unsigned NOT NULL DEFAULT '0.00',
  `integral` int(10) unsigned NOT NULL DEFAULT '0',
  `buyuser` text NOT NULL,
  `page` int(5) unsigned NOT NULL DEFAULT '0',
  `agree` int(10) unsigned NOT NULL DEFAULT '0',
  `disagree` int(10) unsigned NOT NULL DEFAULT '0',
  `sort` int(10) unsigned NOT NULL DEFAULT '0',
  `publisher` varchar(20) NOT NULL,
  `addtime` int(10) unsigned NOT NULL DEFAULT '0',
  `backgroudColour` varchar(45) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `lang` (`lang`,`channel`,`category`,`published`,`audit`,`recycle`),
  KEY `title` (`title`)
) ENGINE=MyISAM AUTO_INCREMENT=88 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC");
E_D("replace into `cms_article` values('62','1','50','','总经理致辞','General manager','','0','General manager','','','','总经理致辞','总经理致辞','<span style=\"font-size:14px;\">&nbsp;尊敬的宾客：<br />\r\n<br />\r\n</span><span style=\"font-size: 14px;\">&nbsp; &nbsp; &nbsp; &nbsp; 欢迎您访问珠江宾馆网站！&nbsp;</span>\r\n<div>\r\n	<span style=\"font-size:14px;\"><br />\r\n	</span></div>\r\n<div>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp; &nbsp; &nbsp; 广州军区珠江宾馆是一家具有军队特色和高星级标准的酒店，位于秀美的珠江水畔，毗邻繁华的珠江新城，风景宜人，交通便利，素有全军绿色营区的美誉。宾馆设计新颖别致，建筑华美，装修典雅，设施先进，设备完善，管理科学，服务周到，是全国十大品牌会议酒店和广州地区首家&ldquo;六常&rdquo;管理样板店。</span></div>\r\n<div>\r\n	<span style=\"font-size:14px;\"><br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp; 我们服务过毛泽东、周恩来、邓小平、江泽民等党和国家的高级领导，接待过十大开国元帅、上百位将军，迎接了航天英雄、奥运健儿、外国元首、国际友人。我们以几十年的丰富经验，打造了出众的服务品牌。我们以专业的态度，专注的精神，竭诚为您提供优质服务。如需帮助，请与我们联系，让网络增进我们的沟通和了解，真诚希望您留下宝贵的意见和建议。</span></div>\r\n<div>\r\n	<span style=\"font-size:14px;\"><br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp; 感谢您的垂顾！珠江宾馆热情期待您的光临！</span></div>\r\n<div>\r\n	<span style=\"font-size:14px;\"><br />\r\n	</span></div>\r\n','','1','0','inc/uploads/article/201307/20130713001022941.jpg','1','1','1','0','0','','963','0.00','0','','0','0','0','0','admin','1373385600','');");
